mapper.select:
SELECT id, noticeTitle, noticeUrl, content, addTime, topTime, noticeType
FROM system_hot_tab
WHERE id = #{id}

mapper.update:
UPDATE system_hot_tab
SET noticeTitle = #{noticeTitle}, noticeType = #{noticeType}, content = #{content}

    ,noticeUrl = #{noticeUrl}

WHERE id = #{id}

UPDATE system_hot_tab
SET topTime = #{topTime}
WHERE id = #{id}
