mapper.select:
SELECT currencyId, currencyShortName, currencyName, addTime
FROM transaction_currency_tab
WHERE currencyId = #{currencyId}

mapper.update:
UPDATE transaction_currency_tab
SET currencyShortName = #{currencyShortName}, currencyName = #{currencyName}, addTime = #{addTime}
WHERE currencyId = #{currencyId}

mapper.select:
SELECT  currencyId, currencyShortName, currencyName, addTime
FROM transaction_currency_tab
WHERE currencyName = #{currencyName}
