mapper.select:
SELECT COUNT(orderNo)
FROM transaction_make_order_tab
WHERE
    1 = 1

        AND currencyName LIKE CONCAT('%', #{currencyName}, '%')


        AND backerAccount LIKE CONCAT('%', #{backerAccount}, '%')


        AND executeStatus = #{executeStatus}


        AND addTime  >=  #{startAddTime}


        AND addTime  <=  #{endAddTime}


        AND executeTime  >=  #{startExecuteTime}


        AND executeTime  <=  #{endExecuteTime}

SELECT orderNo, paymentType, currencyId, currencyName, currencyNumber, currencyTotalPrice,
backerAccount, ipAddress, executeStatus, remark, executeTime, addTime FROM
transaction_make_order_tab
WHERE
    1 = 1

        AND currencyName LIKE CONCAT('%', #{currencyName}, '%')


        AND backerAccount LIKE CONCAT('%', #{backerAccount}, '%')


        AND executeStatus = #{executeStatus}


        AND addTime  >=  #{startAddTime}


        AND addTime  <=  #{endAddTime}


        AND executeTime  >=  #{startExecuteTime}


        AND executeTime  <=  #{endExecuteTime}

ORDER BY addTime DESC
LIMIT #{startNumber}, #{pageSize}
