mapper.select:
        SELECT SUM(transactionTotalPrice * currencyCoefficient) AS totalPrice, SUM(transactionTotalNumber) AS
        totalNumber, currencyId
		FROM transaction_statistics_tab
		WHERE currencyId = #{currencyId}
		  AND addTime  <=  #{todayData}

SELECT COUNT(orderNo)
FROM transaction_statistics_tab
WHERE 1=1

    AND currencyId = #{currencyId}


    AND addTime  >=  #{startTime}


    AND addTime  <=  #{endTime}

SELECT orderNo, currencyId, currencyName, transactionTotalNumber, transactionTotalPrice, currencyCoefficient,
addTime
FROM transaction_statistics_tab
WHERE 1=1

    AND currencyId = #{currencyId}


    AND addTime  >=  #{startTime}


    AND addTime  <=  #{endTime}

ORDER BY addTime DESC, orderNo
LIMIT #{startNumber}, #{pageSize}
