mapper.select:
	SELECT orderNo, pendingOrderNo, userId, userAccount, paymentType, currencyId, currencyName, transactionPrice,
			currencyNumber, currencyTotalPrice, remark, pendTime, addTime
  	FROM transaction_user_deal_tab
  	WHERE userId = #{userId}
  	ORDER BY addTime DESC
	LIMIT #{startNumber}, #{pageSize}

SELECT COUNT(orderNo)
FROM transaction_user_deal_tab
WHERE 1=1

		AND orderNo LIKE CONCAT('%', #{orderNo}, '%')


		AND userAccount LIKE CONCAT('%', #{userAccount}, '%')


		AND paymentType = #{paymentType}


		AND currencyName = #{currencyName}


		AND addTime  >=  #{startAddTime}


		AND addTime  <=  #{endAddTime}


		AND pendTime  >=  #{startPendTime}


		AND pendTime  <=  #{endPendTime}

SELECT orderNo, pendingOrderNo, userId, userAccount, paymentType, currencyId, currencyName, transactionPrice,
	currencyNumber, currencyTotalPrice, remark, pendTime, addTime
FROM transaction_user_deal_tab
WHERE 1=1

		AND orderNo LIKE CONCAT('%', #{orderNo}, '%')


		AND userAccount LIKE CONCAT('%', #{userAccount}, '%')


		AND paymentType = #{paymentType}


		AND currencyName = #{currencyName}


		AND addTime  >=  #{startAddTime}


		AND addTime  <=  #{endAddTime}


		AND pendTime  >=  #{startPendTime}


		AND pendTime  <=  #{endPendTime}

ORDER BY addTime DESC
LIMIT #{startNumber}, #{pageSize}

SELECT COUNT(orderNo)
FROM transaction_user_deal_tab
WHERE userId = #{userId}
